jQuery(document).ready(function($){$('#ticker').ticker({speed:0.10,ajaxFeed:false,feedUrl:false,htmlFeed:true,debugMode:true,controls:true,titleText:nora_dynamic_script.nora_ticker_title,displayType:'reveal',direction:'ltr',fadeInSpeed:900,fadeOutSpeed:300,pauseOnItems:3000,});if(nora_dynamic_script.nora_show_slider==1){$('#main-slider .bx-slider').slick({dots:true,arrows:true,speed:nora_dynamic_script.nora_slider_speed,fade:false,cssEase:'linear',autoplaySpeed:5000,autoplay:true,adaptiveHeight:true,infinite:true,draggable:true,});if(nora_dynamic_script.nora_slider_transition=='true'){$('#main-slider').on('beforeChange',function(event,slick,currentSlide,nextSlide){$('#main-slider .slick-slide .caption-title').removeClass('fadeInDown animated displayNone');$('#main-slider .slick-slide[data-slick-index='+nextSlide+'] .caption-title').addClass('fadeInDown animated');$('#main-slider .slick-slide[data-slick-index='+currentSlide+'] .caption-title').addClass('displayNone');$('#main-slider .slick-slide .caption-content').removeClass('fadeInUp animated displayNone');$('#main-slider .slick-slide[data-slick-index='+nextSlide+'] .caption-content').addClass('fadeInUp animated');$('#main-slider .slick-slide[data-slick-index='+currentSlide+'] .caption-content').addClass('displayNone');$('#main-slider .slick-slide .caption-read-more1').removeClass('zoomIn animated displayNone');$('#main-slider .slick-slide[data-slick-index='+nextSlide+'] .caption-read-more1').addClass('zoomIn animated');$('#main-slider .slick-slide[data-slick-index='+currentSlide+'] .caption-read-more1').addClass('displayNone');});}}});